def dig(var):
  try:
    if var.__class__==[].__class__:
      for i in range(len(var)):
        var[i]=dig(var[i])
      return var
    else:
      if var.__class__==''.__class__:
        return re.compile(r'EMMA:.*\n').sub('',var)
      else:
        return var
  except TypeError:
    return 'could not evaluate the following component: %s' % var

